mapper.sql:
FROM checklist
INNER JOIN user
   ON user.user_idx = checklist.creator_idx


        AND UPPER(checklist.title) LIKE CONCAT('%', UPPER(#{title}), '%')


        AND UPPER(checklist.status) LIKE CONCAT('%', UPPER(#{status}), '%')


        AND (
            EXISTS (
                SELECT 1
                FROM checklist_user_link
                WHERE checklist_user_link.checklist_idx = checklist.checklist_idx
                  AND (
                    checklist_user_link.user_idx = #{user_idx}

                        OR checklist_user_link.team_idx IN #{value}

                  )
            )
        )

mapper.select:
SELECT checklist.checklist_idx , checklist.title , checklist.description , checklist.status , checklist.creator_idx , user.user_name AS creator_name , checklist.created_date , checklist.updated_date FROM checklist INNER JOIN user ON user.user_idx = checklist.creator_idx WHERE checklist.status != 'COMPLETED' AND ( EXISTS ( SELECT 1 FROM checklist_user_link WHERE checklist_user_link.checklist_idx = checklist.checklist_idx AND ( checklist_user_link.user_idx = #{user_idx}
OR checklist_user_link.team_idx IN #{value}
) ) ) ORDER BY FIELD(checklist.status, 'INCOMPLETE', 'COMPLETED') ASC, checklist.checklist_idx DESC LIMIT 10

SELECT daily_stats.log_date , daily_stats.check_count , LAG(daily_stats.check_count, 1, 0) OVER (ORDER BY daily_stats.log_date) AS daily_increase FROM ( SELECT DATE_FORMAT(created_date, '%Y-%m-%d') AS log_date , SUM(IF(is_checked = 'Y', 1, 0)) AS check_count FROM checklist_log WHERE DATE_SUB(NOW(), INTERVAL 1 MONTH) <= created_date
AND item_idx IN ( SELECT item_idx FROM checklist_item WHERE EXISTS ( SELECT 1 FROM checklist_user_link WHERE checklist_user_link.checklist_idx = checklist_item.checklist_idx AND ( checklist_user_link.user_idx = #{user_idx}
OR checklist_user_link.team_idx IN #{value}
) ) )
GROUP BY DATE_FORMAT(created_date, '%Y-%m-%d') ) AS daily_stats ORDER BY daily_stats.log_date

mapper.delete:
DELETE FROM checklist_user_link
WHERE checklist_idx = #{checklist_idx}
  AND user_idx IN #{value}

DELETE FROM checklist_user_link
WHERE checklist_idx = #{checklist_idx}
  AND team_idx IN #{value}
