mapper.update:
UPDATE meeting_attender_link
SET is_attend = #{isAttend}
WHERE link_idx = #{linkIdx}
  AND user_idx = #{userIdx}

mapper.delete:
DELETE FROM meeting_attender_link
WHERE reservation_idx = #{reservation_idx}
  AND user_idx IN #{value}

mapper.update:
UPDATE meeting_room
SET  name = #{name},
     location = #{location},

         capacity = #{capacity}
         capacity = 0

WHERE room_idx = #{roomIdx}
