mapper.select:
SELECT *
FROM schedule
WHERE schedule_idx = #{schedule_idx}

    AND EXISTS (
        SELECT 1
        FROM schedule_attender_link
        WHERE schedule_idx = schedule_idx
        AND user_idx = #{user_idx}
    )

mapper.update:
UPDATE schedule_attender_link
SET is_attend = #{isAttend}
WHERE link_idx = #{linkIdx}
  AND user_idx = #{userIdx}

mapper.delete:
DELETE FROM schedule_attender_link
WHERE schedule_idx = #{schedule_idx}
  AND user_idx IN #{value}
